import React, { useEffect, useState } from "react";
import Doctor from "../Assets/doctor-picture.png"; // Ensure this path is correct
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCalendarCheck, faAngleUp } from "@fortawesome/free-solid-svg-icons";
import { useNavigate } from "react-router-dom";
import "../Styles/Hero.css";

function Hero() {
    const navigate = useNavigate();
    const [goUp, setGoUp] = useState(false);

    const scrollToTop = () => {
        window.scrollTo({ top: 0, behavior: "smooth" });
    };

    const handleBookAppointmentClick = () => {
        navigate("/appointment");
    };

    useEffect(() => {
        const onPageScroll = () => {
            setGoUp(window.scrollY > 600);
        };

        window.addEventListener("scroll", onPageScroll);
        return () => {
            window.removeEventListener("scroll", onPageScroll);
        };
    }, []);

    return (
        <div className="section-container">
            <div className="hero-section row align-items-center">
                <div className="text-section col-md-6">
                    <p className="text-headline">❤️ Health comes first</p>
                    <h2 className="text-title">
                        Find your Doctor and make an Appointment
                    </h2>
                    <p className="text-description">
                        Talk to online doctors and get medical advice, online prescriptions,
                        refills, and medical notes within minutes. On-demand healthcare
                        services at your fingertips.
                    </p>
                    <button
                        className="text-appointment-btn btn btn-primary"
                        type="button"
                        onClick={handleBookAppointmentClick}
                    >
                        <FontAwesomeIcon icon={faCalendarCheck} /> Book Appointment
                    </button>
                    <div className="text-stats">
                        <div className="text-stats-container">
                            <p>145k+</p>
                            <p>Receive Patients</p>
                        </div>
                        <div className="text-stats-container">
                            <p>50+</p>
                            <p>Expert Doctors</p>
                        </div>
                        <div className="text-stats-container">
                            <p>10+</p>
                            <p>Years of Experience</p>
                        </div>
                    </div>
                </div>

                <div className="hero-image-section col-md-6">
                    <img className="hero-image" src={Doctor} alt="Doctor" />
                </div>
            </div>

            <div
                onClick={scrollToTop}
                className={`scroll-up ${goUp ? "show-scroll" : ""}`}
            >
                <FontAwesomeIcon icon={faAngleUp} />
            </div>
        </div>
    );
}

export default Hero;
